import React,{Component} from 'react';
import { Text1,Text2,Text3, Text4 } from './text';
import Row from 'react-bootstrap/esm/Row';
import Col from 'react-bootstrap/esm/Col';
import Carousels from './casroul';
import Untitled6 from './Untitled6.png';
import Untitled7 from './Untitled7.png';
import Eac8 from './Eac8.png';
import Card from "react-bootstrap/Card";
import { Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

class main extends Component {
  render() {
      const myStyle = {
          backgroundImage:`url(${Eac8})`,
          height: "450",
          fontSize: "20px",
          backgroundSize: "cover",
          backgroundRepeat: "no-repeat",
          style:{opacity: 0}
      };
       
  return (
  <> <Container fluid id="about Us" style={myStyle} className='pt-5 pb-2'>
       <h2 className="pt-5 pb-2  text-xxl-center text-white">About Us</h2>
        <Text2/>
       <h2 className="pt-5 text-xxl-center text-white" >Our Expertise</h2>
        <Text1/>         
     </Container>
     
       <Container fluid className='bg-dark'>
       <div><Carousels/></div>
       </Container>
       <Row className="row1 pt-3 pb-3 ps-2 pe-2">
         <Col >
          <Card className=" bg-white" style={{height:450}}>
          <Card.Img src={Untitled6} width={100} height={100} />
             <Card.Title><h2 >Technology Infrastructure</h2></Card.Title> 
              <Card.Text><Text3/></Card.Text>
          </Card>
         
         </Col> 
        <Col >
          <Card className="bg-white " style={{height:450}}>
          <Card.Img src={Untitled7} width={100} height={100} />
            <Card.Title><h2 >Commitment to Excellence</h2></Card.Title>
              <Card.Text><Text4/></Card.Text>
          </Card>
        </Col>
      </Row>
  </>
  );
}}

export default main;
